import decodeMorse from './decodeMorse';
import decodeBits from './decodeBits';

describe('decodeBits', () => {
  it('should decode example', () => {
    expect(
      decodeMorse(
        decodeBits(
          '1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011',
        ),
      ),
    ).toBe('HEY JUDE');
  });

  it('should basic bits encoding', () => {
    expect(decodeMorse(decodeBits('1'))).toBe('E');
    expect(decodeMorse(decodeBits('101'))).toBe('I');
    expect(decodeMorse(decodeBits('10001'))).toBe('EE');
    expect(decodeMorse(decodeBits('10111'))).toBe('A');
    expect(decodeMorse(decodeBits('1110111'))).toBe('M');
  });

  it('should multiple bits per dot handling', () => {
    expect(decodeMorse(decodeBits('111'))).toBe('E');
    expect(decodeMorse(decodeBits('1111111'))).toBe('E');
    expect(decodeMorse(decodeBits('110011'))).toBe('I');
    expect(decodeMorse(decodeBits('111110000011111'))).toBe('I');
    expect(decodeMorse(decodeBits('11111100111111'))).toBe('M');
    expect(decodeMorse(decodeBits('111000111'))).toBe('I');
    // expect(decodeMorse(decodeBits('111000000000111'))).toBe('EE'); // Should pass! Please fix the solution!
    expect(decodeMorse(decodeBits('111000111000111'))).toBe('S');
    expect(
      decodeMorse(
        decodeBits(
          '',
        ),
      ),
    ).toBe('HEY JUDE');
  });

  it('should extra zero handling', () => {
    expect(decodeMorse(decodeBits('01110'))).toBe('E');
    expect(decodeMorse(decodeBits('000000011100000'))).toBe('E');
  });

  it('should long messages', () => {
    expect(
      decodeMorse(
        decodeBits(
          '',
        ),
      ),
    ).toBe('THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.');
    expect(
      decodeMorse(
        decodeBits(
          '',
        ),
      ),
    ).toBe('THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.');
  });
});
